#!/usr/bin/ruby
require 'thor'
require_relative '../lib/lieutenant_governor'

class Governor < Thor

  desc 'init', 'create path helper file'

  # The govern method writes the js generated by the gem to the file
  # The file is to be specified by the user and need not exist
  # @param [String] path to helper file
  def init(path)
    LieutenantGovernor::Generator.generate(path)
  end
end

Governor.start
